class Employee:
    def __init__(self, name):
        self.name = name
        self.manager = None
        self.direct_reports = []

    def add_direct_report(self, employee):
        self.direct_reports.append(employee)
        employee.manager = self

    def get_level(self):
        if self.manager is None:
            return 1
        else:
            return self.manager.get_level() + 1

    def count_subordinates(self):
        count = 0
        for report in self.direct_reports:
            count += 1 + report.count_subordinates()
        return count

    def get_manager_chain(self):
        chain = []
        current = self
        while current is not None:
            chain.append(current.name)
            current = current.manager
        return chain[::-1]


