class Company:
    def __init__(self):
        self.ceo = None

    def set_ceo(self, name):
        self.ceo = Employee(name, 1)

    def add_employee(self, name, supervisor_name):
        new_employee = Employee(name, self.get_level(supervisor_name) + 1)
        self.add_subordinate(new_employee, supervisor_name)
        return new_employee

    def get_level(self, name):
        if self.ceo.name == name:
            return 1
        else:
            return self.find_level(self.ceo, name)

    def find_level(self, supervisor, name):
        if supervisor.name == name:
            return supervisor.level
        for subordinate in supervisor.subordinates:
            level = self.find_level(subordinate, name)
            if level:
                return level + 1
        return None

    def add_subordinate(self, new_employee, supervisor_name):
        if self.ceo.name == supervisor_name:
            self.ceo.add_subordinate(new_employee)
        else:
            self.find_supervisor(self.ceo, new_employee, supervisor_name)

    def find_supervisor(self, supervisor, new_employee, name):
        if supervisor.name == name:
            supervisor.add_subordinate(new_employee)
        else:
            for subordinate in supervisor.subordinates:
                self.find_supervisor(subordinate, new_employee, name)

    def display_organizational_chart(self):
        self.ceo.display_details()

