class Company:
    def __init__(self, ceo):
        self.ceo = ceo

    def get_employees_at_level(self, level):
        employees = []
        self._get_employees_at_level_recursive(self.ceo, level, 1, employees)
        return employees

    def _get_employees_at_level_recursive(self, employee, target_level, current_level, employees):
        if current_level == target_level:
            employees.append(employee)
        else:
            for subordinate in employee.get_subordinates():
                self._get_employees_at_level_recursive(subordinate, target_level, current_level + 1, employees)

    def get_distance_to_ceo(self, employee):
        return self._get_distance_to_ceo_recursive(self.ceo, employee, 0)

    def _get_distance_to_ceo_recursive(self, current_employee, target_employee, distance):
        if current_employee == target_employee:
            return distance
        else:
            for subordinate in current_employee.get_subordinates():
                result = self._get_distance_to_ceo_recursive(subordinate, target_employee, distance + 1)
                if result is not None:
                    return result
            return None

    def get_employees_up_to_ceo(self, employee):
        employees = []
        self._get_employees_up_to_ceo_recursive(self.ceo, employee, employees)
        return employees

    def _get_employees_up_to_ceo_recursive(self, current_employee, target_employee, employees):
        if current_employee == target_employee:
            employees.append(current_employee)
        else:
            for subordinate in current_employee.get_subordinates():
                self._get_employees_up_to_ceo_recursive(subordinate, target_employee, employees)
                if target_employee in employees:
                    break
            if target_employee not in employees:
                employees.append(current_employee)

