def maxSum(nums1, nums2, k):
    n = len(nums1)
    answer = [0] * n
    for i in range(n):
        indices = [j for j in range(n) if nums1[j] < nums1[i]]
        if len(indices) > k:
            indices.sort(key=lambda x: nums2[x], reverse=True)
            indices = indices[:k]
        answer[i] = sum(nums2[j] for j in indices)
    return answer

