def maximize_sum(nums1, nums2, k):
    n = len(nums1)
    ans = [0] * n
    for i in range(n):
        smaller = []
        for j in range(n):
            if nums1[j] < nums1[i]:
                smaller.append(nums2[j])
        smaller.sort(reverse=True)
        ans[i] = sum(smaller[:k])
    return ans

