class SmallRD:
    def __init__(self, sequence, sample_id):
        self.sequence = sequence
        self.sample_id = sample_id

    def has_start_codon(self):
        return self.sequence.startswith('ATG')

    def has_stop_codon(self):
        return self.sequence.endswith(('TAA', 'TAG', 'TGA'))

    def has_motif(self):
        return 'A4wT' in self.sequence

    def has_consecutive_g(self):
        for i in range(len(self.sequence) - 2):
            if self.sequence[i:i+3].count('G') >= 3:
                return True
        return False

    def filter_sequences(self, sequences):
        filtered_sequences = []
        for sequence in sequences:
            small_rd = SmallRD(sequence['sequence'], sequence['sample_id'])
            if small_rd.has_start_codon() and small_rd.has_stop_codon() and small_rd.has_motif() and small_rd.has_consecutive_g():
                filtered_sequences.append({'sample_id': small_rd.sample_id, 'sequence': small_rd.sequence})
        return sorted(filtered_sequences, key=lambda x: x['sample_id'])

