def find_stop_codons(dna_sequence):
    stop_codons = ['TAA', 'TAG', 'TGA']
    stop_codon_indices = []
    for codon in stop_codons:
        index = dna_sequence.find(codon)
        while index != -1:
            stop_codon_indices.append(index)
            index = dna_sequence.find(codon, index + 1)
    return stop_codon_indices

def find_nucleotide_frequency(dna_sequence, nucleotide):
    return dna_sequence.count(nucleotide)

def find_gc_content(dna_sequence):
    gc_count = dna_sequence.count('G') + dna_sequence.count('C')
    return gc_count / len(dna_sequence)

