def validate_dna_sequence(sequence):
    valid_bases = ['A', 'C', 'G', 'T']
    for base in sequence:
        if base not in valid_bases:
            return False
    return True

def find_start_codon(sequence):
    start_codon = 'ATG'
    for i in range(len(sequence) - len(start_codon) + 1):
        if sequence[i:i+len(start_codon)] == start_codon:
            return i
    return -1

def find_stop_codon(sequence):
    stop_codons = ['TAA', 'TAG', 'TGA']
    for i in range(len(sequence) - 2):
        codon = sequence[i:i+3]
        if codon in stop_codons:
            return i
    return -1

def find_poly_g(sequence):
    for i in range(len(sequence) - 2):
        codon = sequence[i:i+3]
        if codon == 'GGG':
            return i
    return -1

def analyze_dna_sequence(sequence):
    if not validate_dna_sequence(sequence):
        print("Invalid DNA sequence")
        return
    start_index = find_start_codon(sequence)
    if start_index == -1:
        print("No start codon found")
        return
    stop_index = find_stop_codon(sequence)
    if stop_index == -1:
        print("No stop codon found")
        return
    poly_g_index = find_poly_g(sequence)
    if poly_g_index!= -1:
        print("Poly-G found at position", poly_g_index)
    print("Start codon found at position", start_index)
    print("Stop codon found at position", stop_index)

sequence = "ATGGGGTAA"
