def longest_subsequence(s, k):
    def backtrack(start, k, max_len, max_str):
        if start == len(s):
            return max_len, max_str
        max_len_so_far = max_len
        max_str_so_far = max_str
        for i in range(start, len(s)):
            for char in 'abcdefghijklmnopqrstuvwxyz':
                if char != s[i]:
                    if k > 0:
                        new_len, new_str = backtrack(i + 1, k - 1, max_len + 1, max_str + char)
                        if new_len > max_len_so_far:
                            max_len_so_far = new_len
                            max_str_so_far = new_str
                else:
                    new_len, new_str = backtrack(i + 1, k, max_len + 1, max_str + char)
                    if new_len > max_len_so_far:
                        max_len_so_far = new_len
                        max_str_so_far = new_str
        return max_len_so_far, max_str_so_far

    return backtrack(0, k, 0, "")[1]

def longest_subsequence_dp(s, k):
    n = len(s)
    dp = [[0] * (k + 1) for _ in range(n + 1)]
    for i in range(1, n + 1):
        for j in range(k + 1):
            if j > 0:
                for char in 'abcdefghijklmnopqrstuvwxyz':
                    if char != s[i - 1]:
                        dp[i][j] = max(dp[i][j], dp[i - 1][j - 1] + 1)
            dp[i][j] = max(dp[i][j], dp[i - 1][j] + (s[i - 1] == 'a'))
    max_len = 0
    for i in range(k + 1):
        max_len = max(max_len, dp[n][i])
    return max_len

