def is_perfect_square(n):
    if n < 0:
        return False
    sqrt = int(n ** 0.5)
    return sqrt * sqrt == n

def is_karatsuba_number(n, k):
    if n < 0:
        return False
    str_n = str(n)
    if len(str_n)!= k:
        return False
    for i in range(k):
        if str_n[i]!= '1' and str_n[i]!= '0':
            return False
    return True

def find_karatsuba_numbers(k):
    karatsuba_numbers = []
    for i in range(2 ** k):
        if is_karatsuba_number(i, k):
            karatsuba_numbers.append(i)
    return karatsuba_numbers

def find_karatsuba_squares(k):
    karatsuba_squares = []
    karatsuba_numbers = find_karatsuba_numbers(k)
    for num in karatsuba_numbers:
        if is_perfect_square(num):
            karatsuba_squares.append(num)
    return karatsuba_squares

def find_greatest_karatsuba_square(k):
    karatsuba_squares = find_karatsuba_squares(k)
    if not karatsuba_squares:
        return -1
    return max(karatsuba_squares)

