def find_longest_substring(s):
    if not s:
        return -1
    max_length = 0
    for i in range(len(s)):
        for j in range(i + 1, len(s) + 1):
            substring = s[i:j]
            if len(set(substring)) == len(substring):
                max_length = max(max_length, len(substring))
    return max_length if max_length != 0 else -1

