def check_string(s):
    stack = []
    bracket_map = {')': '(', '}': '{', ']': '['}
    for char in s:
        if char in bracket_map.values():
            stack.append(char)
        elif char in bracket_map.keys():
            if not stack or stack.pop()!= bracket_map[char]:
                return False
    return not stack

def main():
    test_strings = ["({[]})", "({[}])", "({[]", ")({[]})"]
    for s in test_strings:
        print(f"Is '{s}' a valid bracket sequence? {check_string(s)}")

if __name__ == "__main__":
    main()
