def generate_vowel_sequence(start, end):
    vowels = 'aeiou'
    start_index = vowels.index(start)
    end_index = vowels.index(end)
    if start_index > end_index:
        return vowels[start_index::-1]
    else:
        return vowels[start_index:end_index+1]

def generate_consonant_sequence(start, end):
    consonants = 'bcdfghjklmnpqrstvwxyz'
    start_index = consonants.index(start)
    end_index = consonants.index(end)
    if start_index > end_index:
        return consonants[start_index:end_index-1:-1]
    else:
        return consonants[start_index:end_index+1]

def generate_alphabet_sequence(start, end):
    alphabet = 'abcdefghijklmnopqrstuvwxyz'
    start_index = alphabet.index(start)
    end_index = alphabet.index(end)
    if start_index > end_index:
        return alphabet[start_index:end_index-1:-1]
    else:
        return alphabet[start_index:end_index+1]

