def buildTree(nums):
    if not nums:
        return None
    root = TreeNode(nums[0])
    queue = [root]
    i = 1
    while i < len(nums):
        node = queue.pop(0)
        if i < len(nums) and nums[i] is not None:
            node.left = TreeNode(nums[i])
            queue.append(node.left)
        i += 1
        if i < len(nums) and nums[i] is not None:
            node.right = TreeNode(nums[i])
            queue.append(node.right)
        i += 1
    return root

def findSpecialPathLength(n, nums):
    root = buildTree(nums)
    return longestPath(root, nums)

