def min_special_path(nums):
    n = len(nums)
    dp = [[float('inf')] * n for _ in range(n)]
    for i in range(n):
        dp[i][i] = 0
    for length in range(1, n):
        for i in range(n - length):
            j = i + length
            for k in range(i, j):
                if nums[k] > nums[k + 1]:
                    dp[i][j] = min(dp[i][j], dp[i][k] + dp[k + 1][j] + 1)
                else:
                    dp[i][j] = min(dp[i][j], dp[i][k] + dp[k + 1][j])
    return dp[0][n - 1]

