class Employee:
    def __init__(self, name, manager=None):
        self.name = name
        self.manager = manager
        self.direct_reports = []

    def add_direct_report(self, employee):
        self.direct_reports.append(employee)

    def get_hierarchy_level(self):
        if self.manager is None:
            return 1
        else:
            return self.manager.get_hierarchy_level() + 1

    def get_subordinate_count(self):
        count = 0
        for employee in self.direct_reports:
            count += 1 + employee.get_subordinate_count()
        return count

    def get_management_chain(self):
        chain = []
        current = self
        while current.manager is not None:
            chain.append(current.manager.name)
            current = current.manager
        return chain


class Organization:
    def __init__(self):
        self.employees = {}

    def add_employee(self, name, manager_name=None):
        if name not in self.employees:
            self.employees[name] = Employee(name)
        if manager_name is not None and manager_name in self.employees:
            self.employees[manager_name].add_direct_report(self.employees[name])
            self.employees[name].manager = self.employees[manager_name]

    def get_employee(self, name):
        return self.employees.get(name)

    def print_organizational_hierarchy(self):
        for name, employee in self.employees.items():
            print(f"Name: {name}, Hierarchy Level: {employee.get_hierarchy_level()}, Subordinate Count: {employee.get_subordinate_count()}, Management Chain: {employee.get_management_chain()}")


