class Employee:
    def __init__(self, id, name, manager_id):
        self.id = id
        self.name = name
        self.manager_id = manager_id
        self.subordinates = []

class Organization:
    def __init__(self):
        self.employees = {}

    def add_employee(self, id, name, manager_id):
        employee = Employee(id, name, manager_id)
        self.employees[id] = employee
        if manager_id in self.employees:
            self.employees[manager_id].subordinates.append(id)

    def get_level(self, id):
        def get_level_recursive(id, level):
            if id not in self.employees:
                return 0
            if self.employees[id].manager_id is None:
                return level
            return get_level_recursive(self.employees[id].manager_id, level + 1)
        return get_level_recursive(id, 1)

    def get_subordinate_count(self, id):
        def get_subordinate_count_recursive(id):
            count = 0
            for subordinate_id in self.employees[id].subordinates:
                count += 1 + get_subordinate_count_recursive(subordinate_id)
            return count
        return get_subordinate_count_recursive(id)

    def get_manager_chain(self, id):
        def get_manager_chain_recursive(id):
            chain = []
            while id in self.employees and self.employees[id].manager_id is not None:
                chain.append(self.employees[id].name)
                id = self.employees[id].manager_id
            if id in self.employees:
                chain.append(self.employees[id].name)
            return chain[::-1]
        return get_manager_chain_recursive(id)

