class Employee:
    def __init__(self, name, employee_id, manager=None):
        self.name = name
        self.employee_id = employee_id
        self.manager = manager

class Organization:
    def __init__(self):
        self.employees = {}

    def add_employee(self, name, employee_id, manager_id=None):
        if employee_id in self.employees:
            print("Employee already exists")
            return

        if manager_id and manager_id not in self.employees:
            print("Manager does not exist")
            return

        manager = self.employees.get(manager_id)
        employee = Employee(name, employee_id, manager)
        self.employees[employee_id] = employee

    def get_manager(self, employee_id):
        if employee_id not in self.employees:
            print("Employee does not exist")
            return

        employee = self.employees[employee_id]
        return employee.manager

    def print_organizational_structure(self):
        for employee_id, employee in self.employees.items():
            if employee.manager:
                print(f"{employee.name} (ID: {employee_id}) reports to {employee.manager.name} (ID: {employee.manager.employee_id})")
            else:
                print(f"{employee.name} (ID: {employee_id}) is a top-level manager")

