def find_sequences(dna_sequence, sample_id):
    result = []
    codons = ['TAA', 'TAG', 'TGA']
    for i in range(len(dna_sequence) - 2):
        codon = dna_sequence[i:i+3]
        if codon in codons:
            result.append(sample_id)
            break
    if 'ATG' in dna_sequence:
        result.append(sample_id)
    for i in range(len(dna_sequence) - 2):
        if dna_sequence[i:i+3] == 'GGG' or dna_sequence[i:i+4] == 'GGGG':
            result.append(sample_id)
            break
    return list(set(result))

