import pandas as pd

def process_samples(samples):
    result = []
    for index, row in samples.iterrows():
        id = row['id']
        sequence = row['sequence']
        pattern = row['pattern']
        if pattern == 'simple':
            result.append({'id': id, 'sequence': sequence, 'pattern': pattern, 'order': 'simple'})
        elif pattern == 'repeated':
            order = ''
            for i in range(len(sequence)):
                if sequence[i] == sequence[i+1]:
                    order += sequence[i]
                else:
                    order += sequence[i] + '-'
            result.append({'id': id, 'sequence': sequence, 'pattern': pattern, 'order': order})
        elif pattern == 'alternating':
            order = ''
            for i in range(len(sequence)):
                if sequence[i] == sequence[i+1]:
                    order += sequence[i]
                else:
                    order += sequence[i] + '-'
            result.append({'id': id, 'sequence': sequence, 'pattern': pattern, 'order': order})
        elif pattern == 'complex':
            order = ''
            i = 0
            while i < len(sequence):
                if sequence[i] == sequence[i+1] == sequence[i+2]:
                    order += sequence[i] + sequence[i+1] + sequence[i+2]
                    i += 3
                else:
                    order += sequence[i] + '-'
                    i += 1
            result.append({'id': id, 'sequence': sequence, 'pattern': pattern, 'order': order})
    return pd.DataFrame(result)

samples = pd.DataFrame({
    'id': [1, 2, 3, 4],
    'sequence': ['ATCG', 'GGGG', 'ATGC', 'GGGTTT'],
    'pattern': ['simple', 'repeated', 'alternating', 'complex']
})

print(process_samples(samples))

