import re

def find_pattern(string):
    pattern = r'ATG\s*\(\s*\)\s*-\s*\'\s*\,\s*\'\s*\,\s*\'\s*\(\s*\)\s*-\s*\'\s*\(\s*\'\s*\'\s*\'\s*\)'
    match = re.search(pattern, string)
    if match:
        return match.group()
    else:
        return None

