    memo = {}

    def dp(i, j, k):
        if (i, j, k) in memo:
            return memo[(i, j, k)]
        if i > j:
            return 0
        if i == j:
            return 1
        if k >= 1:
            take = dp(i + 1, j - 1, k - 1) + 2
            not_take = max(dp(i + 1, j, k), dp(i, j - 1, k))
            memo[(i, j, k)] = max(take, not_take)
            return memo[(i, j, k)]
        else:
            if s[i] == s[j]:
