def longest_palindrome(s: str, k: int) -> int:
    def helper(s, left, right, k):
        if left >= right:
            return 0
        if s[left] == s[right]:
            return helper(s, left + 1, right - 1, k)
        if k > 0:
            return 1 + max(helper(s, left + 1, right, k - 1), helper(s, left, right - 1, k - 1))
        return 0

    return len(s) - helper(s, 0, len(s) - 1, k)

def longest_palindrome_dp(s: str, k: int) -> int:
    n = len(s)
    dp = [[0] * n for _ in range(n)]
    for i in range(n - 1, -1, -1):
        dp[i][i] = 1
        for j in range(i + 1, n):
            if s[i] == s[j]:
                dp[i][j] = dp[i + 1][j - 1] + 2
            else:
                dp[i][j] = max(dp[i + 1][j], dp[i][j - 1])
    return n - (k - (n - dp[0][n - 1])) if k >= (n - dp[0][n - 1]) else dp[0][n - 1]

