    def is_palindrome(s):
        return s == s[::-1]

    def min_operations(s):
        operations = 0
        for i in range(len(s) // 2):
            if s[i]!= s[-i - 1]:
                operations += 1
        return operations

    operations = min_operations(s)
    print(f"Minimum operations to make '{s}' a palindrome: {operations}")

    if is_palindrome(s):
        print(f"'{s}' is already a palindrome.")
    else:
        print(f"'{s}' is not a palindrome.")

    # Example usage:
    s = "madam"
    make_palindrome(s)

