def find_max_integer(lst):
    if not lst:
        return None
    return max(lst)

def find_min_integer(lst):
    if not lst:
        return None
    return min(lst)

def find_sum_of_integers(lst):
    if not lst:
        return 0
    return sum(lst)

def find_average_of_integers(lst):
    if not lst:
        return 0
    return sum(lst) / len(lst)

def find_median_of_integers(lst):
    if not lst:
        return None
    sorted_lst = sorted(lst)
    length = len(sorted_lst)
    if length % 2 == 0:
        return (sorted_lst[length // 2 - 1] + sorted_lst[length // 2]) / 2
    else:
        return sorted_lst[length // 2]

def main():
    lst = [1, 2, 3, 4, 5]
    print("Max integer: ", find_max_integer(lst))
    print("Min integer: ", find_min_integer(lst))
    print("Sum of integers: ", find_sum_of_integers(lst))
    print("Average of integers: ", find_average_of_integers(lst))
    print("Median of integers: ", find_median_of_integers(lst))

if __name__ == "__main__":
    main()

