def longest_univalue_path(root):
    if not root:
        return 0, 0
    stack = [(root, 0, 0, 0)]
    max_len = 0
    min_node = float('inf')
    while stack:
        node, parent_val, parent_len, depth = stack.pop()
        if node:
            if node.val == parent_val:
                node_len = parent_len + 1
            else:
                node_len = 1
            max_len = max(max_len, node_len)
            min_node = min(min_node, depth + 1)
            stack.append((node.left, node.val, node_len, depth + 1))
            stack.append((node.right, node.val, node_len, depth + 1))
    return max_len, min_node

