class HierarchyAnalyzer:
    def __init__(self, ceo):
        self.ceo = ceo
        self.employees = {}

    def add_employee(self, employee):
        self.employees[employee.name] = employee

    def calculate_levels(self):
        levels = {}
        def calculate_level(employee, level):
            levels[employee.name] = level
            for subordinate in employee.subordinates:
                calculate_level(subordinate, level + 1)
        calculate_level(self.ceo, 1)
        return levels

    def count_subordinates(self):
        subordinate_counts = {}
        def count_subordinates(employee):
            count = 0
            for subordinate in employee.subordinates:
                count += 1 + count_subordinates(subordinate)
            subordinate_counts[employee.name] = count
            return count
        count_subordinates(self.ceo)
        return subordinate_counts

    def get_manager_chain(self, employee):
        chain = []
        while employee:
            chain.append(employee.name)
            employee = employee.manager
        return list(reversed(chain))

    def analyze_hierarchy(self):
        levels = self.calculate_levels()
        subordinate_counts = self.count_subordinates()
        for employee in self.employees.values():
            print(f"Employee: {employee.name}")
            print(f"Level: {levels[employee.name]}")
            print(f"Subordinate Count: {subordinate_counts[employee.name]}")
            print(f"Manager Chain: {self.get_manager_chain(employee)}")
            print()

