import pandas as pd

def dna_pattern(samples):
    result = samples.assign(
        start_codon=lambda x: x['dna_sequence'].str.contains('ATG'),
        stop_codon=lambda x: x['dna_sequence'].str.contains('TAA|TAG|TGA', case=False),
        atat_pattern=lambda x: x['dna_sequence'].str.contains('ATAT'),
        ggg_pattern=lambda x: x['dna_sequence'].str.contains('G{3,}')
    )
    return result[['sample_id', 'start_codon', 'stop_codon', 'atat_pattern', 'ggg_pattern']].sort_values(by='sample_id')

samples = pd.DataFrame({
    'sample_id': [1, 2, 3, 4, 5],
    'dna_sequence': ['ATGGGGTAA', 'ATGATATGGA', 'TGAATGCGG', 'GGGGATGTA', 'ATGTTAGGG'],
    'specie': ['Human', 'Mouse', 'Rat', 'Dog', 'Cat']
})

print(dna_pattern(samples))

