import pandas as pd

def filter_samples(sample):
    if sample['dna_sequence'].startswith('ATG') and (sample['dna_sequence'].endswith('TAA') or sample['dna_sequence'].endswith('TAG') or sample['dna_sequence'].endswith('TGA')):
        if 'ATAT' in sample['dna_sequence']:
            if 'GGG' in sample['dna_sequence'] or 'MMMM' in sample['dna_sequence']:
                return True
    return False

def process_samples(samples):
    filtered_samples = samples[samples.apply(filter_samples, axis=1)]
    return filtered_samples.sort_values(by='sample_id')

