import pandas as pd

def analyze_dna_sequence(row):
    sequence = row['dna_sequence']
    if sequence.startswith('ATG') and (sequence.endswith('TAA') or sequence.endswith('TAG') or sequence.endswith('TGA')) and 'ATAT' in sequence and 'GGG' in sequence:
        return True
    else:
        return False

def has_consecutive_g(sequence, count):
    for i in range(len(sequence) - count + 1):
        if sequence[i:i+count] == 'G' * count:
            return True
    return False

def filter_samples(df):
    df['has_start_codon'] = df.apply(lambda row: row['dna_sequence'].startswith('ATG'), axis=1)
    df['has_stop_codon'] = df.apply(lambda row: row['dna_sequence'].endswith('TAA') or row['dna_sequence'].endswith('TAG') or row['dna_sequence'].endswith('TGA'), axis=1)
    df['has_atat_motif'] = df.apply(lambda row: 'ATAT' in row['dna_sequence'], axis=1)
    df['has_consecutive_gs'] = df.apply(lambda row: has_consecutive_g(row['dna_sequence'], 3), axis=1)
    df['is_canonical'] = df.apply(lambda row: row['has_start_codon'] and row['has_stop_codon'] and row['has_atat_motif'] and row['has_consecutive_gs'], axis=1)
    return df[df['is_canonical']][['sample_id', 'dna_sequence', 'mintage']].sort_values(by='sample_id')

