    'sample_id': [1, 2, 3, 4, 5],
    'dna_sequence': ['ATGGGGTAA', 'ATGATATGGG', 'ATGATATGGGGTGA', 'ATGATATGGGGTAG', 'ATGATATGGGGTAA'],
    'mintage': [10, 20, 30, 40, 50]
})

result = filter_samples(Sampling)
