            return 0

        visited.add(node.value)
        max_length = 0

        for child in node.children:
            max_length = max(max_length, dfs(child, visited))

        visited.remove(node.value)
        return max_length + 1

    return dfs(root, set()) - 1

def build_tree(n, edges, nums):
    nodes = [TreeNode(nums[i]) for i in range(n)]
    for u, v in edges:
        nodes[u].children.append(nodes[v])
        nodes[v].children.append(nodes[u])
    return nodes[0]

def longest_special_path_in_tree(n, edges, nums):
