def longest_special_path(n, edges, nums):
    from collections import defaultdict

    # Build the tree as an adjacency list
    tree = defaultdict(list)
    for u, v in edges:
        tree[u].append(v)
        tree[v].append(u)

    longest_path = 0

    def dfs(node, parent, current_length):
        nonlocal longest_path
        longest_path = max(longest_path, current_length)

        for neighbor in tree[node]:
            if neighbor != parent:
                if nums[neighbor] == nums[node]:
                    dfs(neighbor, node, current_length + 1)
                else:
                    dfs(neighbor, node, 1)

    for i in range(n):
        dfs(i, -1, 1)

    return longest_path

