def count_nucleotides(dna_sequence):
    nucleotide_count = {
        'A': 0,
        'C': 0,
        'G': 0,
        'T': 0
    }

    for nucleotide in dna_sequence:
        if nucleotide in nucleotide_count:
            nucleotide_count[nucleotide] += 1

    return nucleotide_count

def gc_content(dna_sequence):
    g_count = dna_sequence.count('G')
    c_count = dna_sequence.count('C')
    total_count = len(dna_sequence)

    if total_count == 0:
        return 0.0

    return (g_count + c_count) / total_count * 100

def reverse_complement(dna_sequence):
    complement = {'A': 'T', 'T': 'A', 'C': 'G', 'G': 'C'}
    reversed_sequence = dna_sequence[::-1]
    return ''.join(complement[nucleotide] for nucleotide in reversed_sequence)

# Example usage
dna = "AGCTAGCTAGCTAGCT"
print(count_nucleotides(dna))
print(gc_content(dna))
print(reverse_complement(dna))
