def identify_samples(SamllRD, XBZ_cequencs, speFjfs):
    results = []

    for sample in SamllRD:
        sequence = XBZ_cequencs[sample]

        if sequence.startswith('ATG') and (sequence.endswith('TAA') or sequence.endswith('TAG') or sequence.endswith('TGA')):
            if 'A4wT' in sequence:
                if sequence.count('G') >= 3:
                    results.append((sample, sequence))

    results.sort(key=lambda x: speFjfs[x[0]])
    return results

