def analyze_dna_sequence(dna_sequence):
    results = {
        "start_codon": None,
        "stop_codons": [],
        "contains_zinc_finger": False,
        "consecutive_gs": 0
    }

    # Check for start codon 'ATG'
    if 'ATG' in dna_sequence:
        results["start_codon"] = dna_sequence.index('ATG')

    # Check for stop codons 'TAA', 'TAG', 'TGA'
    stop_codons = ['TAA', 'TAG', 'TGA']
    for codon in stop_codons:
        if codon in dna_sequence:
            results["stop_codons"].append(dna_sequence.index(codon))

    # Check for zinc finger motif 'zHAT'
    if 'zHAT' in dna_sequence:
        results["contains_zinc_finger"] = True

    # Count consecutive 'G's
    g_count = 0
    max_g_count = 0
    for nucleotide in dna_sequence:
        if nucleotide == 'G':
            g_count += 1
            if g_count > max_g_count:
                max_g_count = g_count
        else:
            g_count = 0
    results["consecutive_gs"] = max_g_count

    return results

