def longest_special_path(n, nums):
    from collections import defaultdict, deque

    graph = defaultdict(list)
    for i in range(n):
        for j in range(i + 1, n):
            if nums[i] == nums[j]:
                graph[i].append(j)
                graph[j].append(i)

    def bfs(start):
        visited = set()
        queue = deque([(start, 0)])
        visited.add(start)
        max_length = 0
        min_nodes = float('inf')

        while queue:
            node, length = queue.popleft()
            if length > max_length:
                max_length = length
                min_nodes = 1
            elif length == max_length:
                min_nodes += 1

            for neighbor in graph[node]:
                if neighbor not in visited:
                    visited.add(neighbor)
                    queue.append((neighbor, length + 1))

        return max_length, min_nodes

    longest_length = 0
    min_nodes = float('inf')

    for i in range(n):
        if nums.count(nums[i]) > 2:
            length, nodes = bfs(i)
            if length > longest_length:
                longest_length = length
                min_nodes = nodes
            elif length == longest_length:
                min_nodes += nodes

    return longest_length, min_nodes if longest_length > 0 else (0, 0)

