def longest_special_path(tree):
    def dfs(node, parent, count):
        if node is None:
            return count

        if tree[node] == 1:
            count += 1
        else:
            count = 0

        max_count = count
        for child in children[node]:
            if child != parent:
                max_count = max(max_count, dfs(child, node, count))

        return max_count

    n = len(tree)
    children = [[] for _ in range(n)]

    for i in range(n):
        if tree[i] == 1:
            if i > 0:
                children[i].append(i - 1)
                children[i - 1].append(i)
            if i < n - 1:
                children[i].append(i + 1)
                children[i + 1].append(i)

    return dfs(0, -1, 0)

