class Employee:
    def __init__(self, name, level, manager=None):
        self.name = name
        self.level = level
        self.manager = manager
        self.subordinates = []

    def add_subordinate(self, employee):
        self.subordinates.append(employee)

def build_hierarchy(employees):
    employee_dict = {emp.name: emp for emp in employees}
    for emp in employees:
        if emp.manager:
            employee_dict[emp.manager].add_subordinate(emp)
    return employee_dict

def get_employee_level(employee):
    return employee.level

def count_subordinates(employee):
    count = len(employee.subordinates)
    for sub in employee.subordinates:
        count += count_subordinates(sub)
    return count

def get_manager_chain(employee):
    chain = []
    while employee.manager:
        chain.append(employee.manager)
        employee = employee_dict[employee.manager]
    return chain

