def analyze_hierarchy(employee_data):
    hierarchy = {}

    for employee in employee_data:
        name = employee['name']
        level = employee['level']
        reports_to = employee['reports_to']

        if level not in hierarchy:
            hierarchy[level] = []
        hierarchy[level].append(name)

        if reports_to not in hierarchy:
            hierarchy[reports_to] = []

    # Determine levels
    levels = sorted(hierarchy.keys())

    # Reports to CEO are at level 2
    ceo_level = 2
    ceo_reports = hierarchy.get(ceo_level, [])

    # Total employees under each manager including indirect reports
    total_under = {}

    for level in levels:
        total_under[level] = len(hierarchy[level])

    # Chain of managers up to the CEO
    chain = {}

    for level in levels:
        if level < ceo_level:
            chain[level] = hierarchy[level]

    return {
        "levels": levels,
        "ceo_reports": ceo_reports,
        "total_under": total_under,
        "chain": chain
    }

# Example usage
employee_data = [
    {'name': 'Alice', 'level': 1, 'reports_to': 'CEO'},
    {'name': 'Bob', 'level': 2, 'reports_to': 1},
    {'name': 'Charlie', 'level': 2, 'reports_to': 1},
    {'name': 'David', 'level': 3, 'reports_to': 2},
    {'name': 'Eve', 'level': 3, 'reports_to': 2}
]

result = analyze_hierarchy(employee_data)
print(result)

