class Employee:
    def __init__(self, name, position):
        self.name = name
        self.position = position
        self.subordinates = []

    def add_subordinate(self, employee):
        self.subordinates.append(employee)

    def count_subordinates(self):
        count = len(self.subordinates)
        for subordinate in self.subordinates:
            count += subordinate.count_subordinates()
        return count

class Organization:
    def __init__(self, ceo_name):
        self.ceo = Employee(ceo_name, "CEO")

    def add_employee(self, manager_name, employee_name, position):
        manager = self.find_employee(self.ceo, manager_name)
        if manager:
            new_employee = Employee(employee_name, position)
            manager.add_subordinate(new_employee)

    def find_employee(self, current_employee, name):
        if current_employee.name == name:
            return current_employee
        for subordinate in current_employee.subordinates:
            found = self.find_employee(subordinate, name)
            if found:
                return found
        return None

    def get_hierarchy(self):
        return self._get_hierarchy(self.ceo, 0)

    def _get_hierarchy(self, employee, level):
        hierarchy = "  " * level + f"{employee.position}: {employee.name}\n"
        for subordinate in employee.subordinates:
            hierarchy += self._get_hierarchy(subordinate, level + 1)
        return hierarchy

# Example usage
org = Organization("Alice")
org.add_employee("Alice", "Bob", "Manager")
org.add_employee("Alice", "Charlie", "Manager")
org.add_employee("Bob", "David", "Employee")
org.add_employee("Bob", "Eve", "Employee")
org.add_employee("Charlie", "Frank", "Employee")

print(org.get_hierarchy())
print("Total subordinates under Alice:", org.ceo.count_subordinates())
