def longest_special_path(root, nums):
    def dfs(node, path_values):
        if not node:
            return 0, float('inf')

        path_values[node.value] += 1
        max_length = 0
        min_value = float('inf')

        if path_values[node.value] <= 2:
            for child in node.children:
                length, min_val = dfs(child, path_values)
                if length > max_length:
                    max_length = length
                    min_value = min_val
                elif length == max_length:
                    min_value = min(min_value, min_val)

            max_length += 1
            min_value = min(min_value, node.value)

        path_values[node.value] -= 1
        return max_length, min_value

    root_node = TreeNode(nums[root])
    for i in range(len(nums)):
        if i != root:
            root_node.children.append(TreeNode(nums[i]))

    path_values = defaultdict(int)
    length, min_value = dfs(root_node, path_values)

    return length, min_value

