def find_special_paths(n, nums):
    from collections import defaultdict

    # Build the tree
    tree = defaultdict(list)
    for i in range(1, n):
        tree[i // 2].append(i)

    def dfs(node, path, visited):
        if len(path) > 0 and path[-1] == nums[node]:
            return  # Skip if the last value is the same as the current
        if visited[nums[node]] >= 2:
            return  # Skip if the current value has appeared twice already

        visited[nums[node]] += 1
        path.append(nums[node])

        if len(tree[node]) == 0:  # Leaf node
            if len(path) > 0:
                special_paths.append((sum(path), len(path)))
        else:
            for child in tree[node]:
                dfs(child, path, visited)

        visited[nums[node]] -= 1
        path.pop()

    special_paths = []
    visited = [0] * (max(nums) + 1)
    dfs(0, [], visited)

    if special_paths:
        max_duration = max(special_paths, key=lambda x: x[0])
        return max_duration
    return (0, 0)

