        if node >= n:
            return 0, 0

        if nums[node] in visited:
            return 0, float('inf')

        visited.add(nums[node])
        length, min_clients = dfs(node + 1, visited)
        visited.remove(nums[node])

        return length + 1, min(min_clients, len(visited))

