def longest_path_and_min_nodes(n, nums):
    from collections import defaultdict, deque

    graph = defaultdict(list)
    for i in range(n):
        for j in range(i + 1, n):
            if nums[j] <= 2 * nums[i]:
                graph[i].append(j)

    max_length = 0
    min_nodes = float('inf')

    def bfs(start):
        queue = deque([(start, nums[start], 1)])  # (current_node, current_length, node_count)
        local_max_length = 0
        local_min_nodes = float('inf')

        while queue:
            node, length, count = queue.popleft()
            if length > local_max_length:
                local_max_length = length
                local_min_nodes = count
            elif length == local_max_length:
                local_min_nodes = min(local_min_nodes, count)

            for neighbor in graph[node]:
                queue.append((neighbor, length + nums[neighbor], count + 1))

        return local_max_length, local_min_nodes

    for i in range(n):
        length, count = bfs(i)
        if length > max_length:
            max_length = length
            min_nodes = count
        elif length == max_length:
            min_nodes = min(min_nodes, count)

    return max_length, min_nodes

