class TreeNode:
    def __init__(self, value):
        self.value = value
        self.children = []

def build_tree(nums):
    nodes = [TreeNode(i) for i in range(len(nums))]
    for i in range(len(nums)):
        if nums[i] != -1:
            nodes[nums[i]].children.append(nodes[i])
    return nodes[0] if nodes else None

def find_paths(node, path, visited, all_paths):
    if node is None:
        return
    if node.value in visited:
        return
    visited.add(node.value)
    path.append(node.value)

    if not node.children:
        all_paths.append(list(path))
    else:
        for child in node.children:
            find_paths(child, path, visited, all_paths)

    path.pop()
    visited.remove(node.value)

def longest_unique_path(root):
    all_paths = []
    find_paths(root, [], set(), all_paths)
    max_length = 0
    min_nodes = float('inf')

    for path in all_paths:
        if len(path) > max_length:
            max_length = len(path)
            min_nodes = len(path)
        elif len(path) == max_length:
            min_nodes = min(min_nodes, len(path))

    return max_length, min_nodes

def main(nums):
    root = build_tree(nums)
    return longest_unique_path(root)

