class Employee:
    def __init__(self, name, manager=None):
        self.name = name
        self.manager = manager
        self.subordinates = []

    def add_subordinate(self, employee):
        self.subordinates.append(employee)

def hierarchy_degree(employee, level=1):
    if employee.manager is None:
        return level
    return hierarchy_degree(employee.manager, level + 1)

def subordinate_count(employee):
    count = len(employee.subordinates)
    for subordinate in employee.subordinates:
        count += subordinate_count(subordinate)
    return count

def manager_chain(employee):
    chain = []
    while employee.manager is not None:
        chain.append(employee.manager.name)
        employee = employee.manager
    return chain

def analyze_organization(employees):
    results = {}
    for employee in employees:
        results[employee.name] = {
            "Hierarchy Degree": hierarchy_degree(employee),
            "Subordinate Count": subordinate_count(employee),
            "Manager Chain": manager_chain(employee)
        }
    return results

