def get_hierarchy_info(employee_dict):
    hierarchy_info = {}

    def calculate_levels(emp, level=1):
        hierarchy_info[emp.name] = {
            'level': level,
            'subordinate_count': 0,
            'manager_chain': []
        }
        for sub in emp.subordinates:
            calculate_levels(sub, level + 1)
            hierarchy_info[emp.name]['subordinate_count'] += 1 + hierarchy_info[sub.name]['subordinate_count']

    def build_manager_chain(emp):
