        for subordinate in self.subordinates:
            count += subordinate.get_subordinate_count()
        return count

    def get_management_chain(self):
        chain = [self.name]
        if self.level > 1:
            chain += self.manager.get_management_chain()
        return chain

class Organization:
    def __init__(self):
        self.employees = {}

    def add_employee(self, name, level, manager_name=None):
        employee = Employee(name, level)
        self.employees[name] = employee
        if manager_name:
            manager = self.employees[manager_name]
            manager.add_subordinate(employee)
            employee.manager = manager

    def analyze(self):
        for employee in self.employees.values():
            subordinate_count = employee.get_subordinate_count()
            management_chain = employee.get_management_chain()
            print(f"Employee: {employee.name}, Level: {employee.level}, Subordinate Count: {subordinate_count}, Management Chain: {' -> '.join(management_chain)}")

# Example usage
