org.add_employee("CTO", 2, "CEO")
org.add_employee("CFO", 2, "CEO")
org.add_employee("Dev1", 3, "CTO")
org.add_employee("Dev2", 3, "CTO")
org.add_employee("Accountant", 3, "CFO")

